$data = $csv | ConvertFrom-Csv -Delim ','

foreach ($row in $data){

    $line = [System.Collections.ArrayList]@(
        "new Geomaterial {",

        # Replace periods with dashes in HierarchyKey
        "HierarchyKey=`"$($row.HierarchyKey.Replace(".","-"))`",",

        # Escape strings
        "GeoMaterial=`"$($row.GeoMaterial.Replace('`"',"`'"))`",",
        "IndentedName=`"$($row.IndentedName.Replace('`"',"`'"))`",",
        "Definition=`"$($row.Definition.Replace('`"',"`'"))`",",
        "},"
     ) 

     Write-Host $line
}