using Geomapmaker.Models;
using System.Collections.ObjectModel;

namespace Geomapmaker.Data
{
    public class GeoMaterials
    {
        public static ObservableCollection<Geomaterial> GeoMaterialOptions => new ObservableCollection<Geomaterial>()
        {
            new Geomaterial { Name="Sedimentary material", Description="An aggregation of particles deposited by gravity, air, water, or ice, or as accumulated by other natural agents operating at Earth’s surface such as chemical precipitation or secretion by organisms. May include uncon-solidated material (sediment) and (or) sedimentary rock. Does not include sedimentary material directly deposited as a result of volcanic activity." },
            new Geomaterial { Name="Sediment", Description="Unconsolidated material (sediment) composed of particles deposited by gravity, air, water, or ice, or as accumulated by other natural agents operating at Earth’s surface such as chemical precipitation or secretion by organ-isms. Does not include sedimentary material directly deposited as a result of volcanic activity." },
            new Geomaterial { Name="Clastic sediment", Description="Sediment formed by weathering and erosion of preexisting rocks or minerals; eroded particles or “clasts” are transported and deposited by gravity, air, water, or ice." },
            new Geomaterial { Name="Sand and gravel of unspecified origin", Description="Sediment composed mostly of sand and (or) gravel, formed by weath-ering and erosion of preexisting rocks or minerals; eroded particles or “clasts” are transported and deposited by gravity, air, water, or ice." },
            new Geomaterial { Name="Silt and clay of unspecified origin", Description="Sediment composed mostly of silt and (or) clay, formed by weathering and erosion of preexisting rocks or minerals; eroded particles or “clasts” are transported and deposited by gravity, air, water, or ice." },
            new Geomaterial { Name="Alluvial sediment", Description="Unconsolidated material deposited by streams or other bodies of running water as sorted or semisorted sediment in streambed, or on its floodplain or delta, or as cone or fan at base of mountain slope. Grain size varies from clay to gravel." },
            new Geomaterial { Name="Alluvial sediment, mostly coarse-grained", Description="Unconsolidated material deposited by streams or other bodies of running water as sorted or semisorted sediment in streambed, or on its floodplain or delta, or as cone or fan at base of mountain slope. Sediment is mostly sand, gravel, and coarser material but may also contain some silt and clay." },
            new Geomaterial { Name="Alluvial sediment, mostly fine-grained", Description="Unconsolidated material deposited by streams or other bodies of running water as sorted or semisorted sediment in streambed, or on its floodplain or delta, or as cone or fan at base of mountain slope. Sediment is mostly silt and clay but may also contain some sand and gravel." },
            new Geomaterial { Name="Glacial till", Description="Mostly unsorted and unstratified material, generally unconsolidated, deposited directly by and underneath or adjacent to glacier without subsequent reworking by meltwater. Consists of heterogeneous mixture of clay, silt, sand, gravel, and boulders, ranging widely in size and shape." },
            new Geomaterial { Name="Glacial till, mostly sandy", Description="Mostly unsorted and unstratified material, generally unconsolidated, deposited directly by and underneath or adjacent to glacier without subsequent reworking by meltwater. Consists of het-erogeneous mixture of clay, silt, sand, gravel, and boulders, ranging widely in size and shape. Relatively sandy in texture." },
            new Geomaterial { Name="Glacial till, mostly silty", Description="Mostly unsorted and unstratified material, generally unconsolidated, deposited directly by and underneath or adjacent to glacier without subsequent reworking by meltwater. Consists of het-erogeneous mixture of clay, silt, sand, gravel, and boulders, ranging widely in size and shape. Relatively loamy (silty) in texture." },
            new Geomaterial { Name="Glacial till, mostly clayey", Description="Mostly unsorted and unstratified material, generally unconsolidated, deposited directly by and underneath or adjacent to glacier without subsequent reworking by meltwater. Consists of het-erogeneous mixture of clay, silt, sand, gravel, and boulders, ranging widely in size and shape. Relatively clayey in texture." },
            new Geomaterial { Name="Ice-contact and ice-marginal sediment", Description="Mostly sand-, silt-, and gravel-sized particles or “clasts” derived from rock or preexisting sediment that has been eroded and transported by glaciers. As glacier melted, material was deposited by running water essentially in contact with glacial ice or was transported and deposited by glacially fed streams. Includes sediment deposited into water bodies adjacent to glacier." },
            new Geomaterial { Name="Ice-contact and ice-marginal sediment, mostly coarse-grained", Description="Mostly sand- and gravel-sized particles or “clasts”, with lesser amounts of silt and clay, derived from rock or preexisting sediment that has been eroded and transported by glaciers. As glacier melted, material was deposited by running water essentially in contact with glacial ice or was transported and deposited by glacially fed streams. Includes sediment deposited into water bodies adjacent to glacier." },
            new Geomaterial { Name="Ice-contact and ice-marginal sediment, mostly fine-grained", Description="Mostly silt- and clay-sized particles or “clasts”, with lesser amounts of sand and gravel, derived from rock or preexisting sediment that has been eroded and transported by glaciers. As glacier melted, material was deposited by running water essentially in contact with glacial ice or was transported and deposited by glacially fed streams. Includes sediment deposited into water bodies adjacent to glacier." },
            new Geomaterial { Name="Eolian sediment", Description="Silt- and sand-sized sediment, deposited by wind." },
            new Geomaterial { Name="Dune sand", Description="Mostly sand-sized sediment, deposited by wind. Typically characterized by various dune landforms." },
            new Geomaterial { Name="Loess", Description="Silty sediment, deposited by wind, commonly near glacial margin." },
            new Geomaterial { Name="Lacustrine sediment", Description="Mostly well-sorted and well-bedded material that ranges in grain size from clay to gravel, deposited in perennial to intermittent lakes. Much of sediment is derived from material eroded and trans-ported by streams. Includes deposits of lake-marginal beaches and deltas." },
            new Geomaterial { Name="Lacustrine sediment, mostly coarse-grained", Description="Mostly well-sorted and well-bedded material, generally sand and gravel sized, with lesser amounts of silt and clay, deposited in perennial to intermittent lakes. Much of sediment is derived from material eroded and transported by streams. Mostly deposits of lake-marginal beaches and deltas." },
            new Geomaterial { Name="Lacustrine sediment, mostly fine-grained", Description="Mostly well-sorted and well-bedded material, generally silt and clay sized, with lesser amounts of sand, deposited in perennial to intermittent lakes." },
            new Geomaterial { Name="Playa sediment", Description="Fine-grained clastic sediment and evaporitic salts, deposited in ephemeral lakes in centers of undrained basins. Includes material deposited in playas, mud flats, salt flats, and adjacent saline marshes. Gener-ally interbedded with eolian sand and with lacustrine sediment deposited during wetter climatic periods; com-monly intertongues upslope with sediment deposited by alluvial fans." },
            new Geomaterial { Name="Coastal zone sediment", Description="Mud and sand, with lesser amounts of gravel, deposited on beaches, on barrier islands, or in nearshore-marine, deltaic, or various low-energy shoreline (mud flat, tidal flat, sabka, algal flat) environments." },
            new Geomaterial { Name="Coastal zone sediment, mostly coarse-grained", Description="Mostly sand, silt, and gravel, deposited on beaches, in dunes, and in shallow-marine and related alluvial environments." },
            new Geomaterial { Name="Coastal zone sediment, mostly fine-grained", Description="Mostly clay and silt, deposited in lagoons, tidal flats, backbarriers, and coastal marshes." },
            new Geomaterial { Name="Marine sediment", Description="Mud and sand, deposited in various marine environments. May originate from erosion of rocks and sediment or may be derived from marine organisms (of carbonate or siliceous composition)." },
            new Geomaterial { Name="Marine sediment, mostly coarse-grained", Description="Mud, sand, and gravel eroded from rocks and sediment on land, transported by streams, and deposited in marine deltas and basins. Mostly siliceous in composition." },
            new Geomaterial { Name="Marine sediment, mostly fine-grained", Description="Mostly clay- and silt-sized sediment, deposited in relatively deep, quiet water, far removed from areas where coarser grained clastic sediments are washed into marine environ-ment. Includes sediment derived from marine organisms." },
            new Geomaterial { Name="Mass movement sediment", Description="Sediment formed by downslope transport of particles or “clasts” produced by weathering and breakdown of underlying rock, sediment, and (or) soil. Composed of poorly sorted and poorly stratified material that ranges in size from clay to boulders. Includes colluvial, landslide, talus, and rock-avalanche deposits." },
            new Geomaterial { Name="Colluvium and other widespread mass-movement sediment", Description="Sediment formed by slow, relatively widespread, downslope transport of particles or “clasts” produced by weathering and breakdown of underlying rock, sediment, and (or) soil. Composed of poorly sorted and poorly stratified material that ranges in size from clay to boulders." },
            new Geomaterial { Name="Debris flows, landslides, and other localized mass-movement sediment", Description="Sediment formed by relatively localized, downslope transport of particles or “clasts” produced by weathering and breakdown of underlying rock, sediment, and (or) soil. Composed of poorly sorted and poorly stratified material that ranges in size from clay to boulders. Speed of downslope transport ranges from rapid to imperceptible." },
            new Geomaterial { Name="Residual material", Description="Unconsolidated material, developed in place by weathering of underlying rock or sediment. Usually forms relatively thin surface layer that conceals unweathered or partly altered source material. Material from which soils are formed." },
            new Geomaterial { Name="Carbonate sediment", Description="Sediment formed by biotic or abiotic precipitation from aqueous solution of carbonates of calcium, magnesium, or iron (for example, limestone, dolomite)." },
            new Geomaterial { Name="Peat and muck", Description="Unconsolidated material, principally composed of plant remains, with lesser amounts of fine-grained clastic sediment. Deposited in water-saturated environment such as swamp, marsh, or bog. With lithification, such material becomes coal." },
            new Geomaterial { Name="Sedimentary rock", Description="Consolidated material (rock) composed of particles transported and deposited by gravity, air, water, or ice, or accumulated by other natural agents operating at Earth’s surface, such as chemical precipitation or secretion by organisms. Does not here include sedimentary material directly deposited as result of volcanic activity." },
            new Geomaterial { Name="Clastic sedimentary rock", Description="Sedimentary rock, composed predominantly of particles or “clasts” derived by ero-sion, weathering, or mass-wasting of preexisting rock and deposited by gravity, air, water, or ice." },
            new Geomaterial { Name="Conglomerate", Description="Sedimentary rock, composed predominantly of particles or “clasts” derived by erosion and weathering of preexisting rock; contains more than 30 percent gravel-sized clasts." },
            new Geomaterial { Name="Sandstone", Description="Sedimentary rock, composed predominantly of particles or “clasts” derived by erosion and weather-ing of preexisting rock; consists mostly of sand-sized particles, with or without fine-grained matrix of silt or clay." },
            new Geomaterial { Name="Mostly sandstone", Description="Mostly sandstone, interbedded with other sedimentary rocks that locally may include con-glomerate and finer grained clastic rocks (mudstone), carbonates, and (or) coal." },
            new Geomaterial { Name="Sandstone and mudstone", Description="Sandstone and mudstone (including shale and siltstone), in approximately equal (or unspecified) proportions." },
            new Geomaterial { Name="Mudstone", Description="Sedimentary rock, composed predominantly of particles or “clasts” derived by erosion and weathering of preexisting rock; consists mostly of mud (that is, silt- and clay-sized particles). Includes shale and siltstone." },
            new Geomaterial { Name="Mostly mudstone", Description="Mostly mudstone, interbedded with other sedimentary rocks that locally may include coarser grained clastic rocks (sandstone, conglomerate), carbonates, and (or) coal." },
            new Geomaterial { Name="Carbonate rock", Description="Sedimentary rock, consisting chiefly of carbonate minerals such as limestone or dolomite." },
            new Geomaterial { Name="Limestone", Description="Carbonate sedimentary rock, consisting chiefly of calcite." },
            new Geomaterial { Name="Dolomite", Description="Carbonate sedimentary rock, consisting chiefly of dolomite. Although “dolostone” is the proper analog to “limestone”, it has not often been applied to dolomitic units." },
            new Geomaterial { Name="Mostly carbonate rock", Description="Mostly carbonate rock, interbedded with other sedimentary rock types." },
            new Geomaterial { Name="Chert", Description="Sedimentary rock, composed chiefly of microcrystalline or cryptocrystalline quartz." },
            new Geomaterial { Name="Evaporitic rock", Description="Sedimentary rock, composed primarily of minerals produced by evaporation of saline solution. Examples include gypsum, anhydrite, other diverse sulfates, halite (rock salt), primary dolomite, and rocks composed of various nitrates and borates." },
            new Geomaterial { Name="Iron-rich sedimentary rock", Description="Sedimentary rock, in which at least half (by volume) of observed minerals are iron bearing (hematite, magnetite, limonite group minerals, siderite, iron sulfides)." },
            new Geomaterial { Name="Coal and lignite", Description="Organic-rich sedimentary rock, formed from compaction and alteration of plant remains. Coal is consolidated, harder, black rock. Lignite is semiconsolidated, brown to black, earthy material that may contain large particles of recognizable plant parts and tends to crack upon drying." },
            new Geomaterial { Name="Sedimentary and extrusive igneous material", Description="Either (1) sedimentary rock and (or) unconsolidated material (sedi-ment) and extrusive igneous material (volcanic rock and [or] sediment) or (2) volcanic rock and (or) sediment and such material after erosion and redeposition." },
            new Geomaterial { Name="Igneous rock", Description="Rock and fragmental material that solidified from molten or partly molten material (magma)." },
            new Geomaterial { Name="Extrusive igneous material", Description="Molten material that was erupted onto Earth’s surface, fusing into rock or remaining as unconsolidated particles. Includes pyroclastic flows, air-fall tephra, lava flows, and volcanic mass flows." },
            new Geomaterial { Name="Volcaniclastic (fragmental) material", Description="Rock and unconsolidated material consisting of particles or “clasts” that were formed by volcanic explosion or aerial expulsion from volcanic vent." },
            new Geomaterial { Name="Pyroclastic flows", Description="Hot ash, pumice, and rock fragments erupted from volcano or caldera. Material moves downslope commonly in chaotic flows; once deposited, hot fragments may compact under their own weight and weld together." },
            new Geomaterial { Name="Felsic-composition pyroclastic flows", Description="Hot ash, pumice, and rock fragments erupted from volcano or caldera. Material moves downslope commonly in chaotic flows; once deposited, hot fragments may compact under their own weight and weld together. Because of their high-silica content and resulting high viscosity, parental magmas tend to erupt explosively. Includes rhyolite, dacite, trachyte, latite; rocks are commonly light-colored." },
            new Geomaterial { Name="Intermediate-composition pyroclastic flows", Description="Hot ash, pumice, and rock fragments erupted from volcano. Material moves downslope commonly in chaotic flows; once deposited, hot fragments may compact under their own weight and weld together. Parental magma commonly erupts from stratovolcanoes as thick lava flows but also can generate strong explosive eruptions to form pyroclastic flows. Includes rocks that are, in color and mineral composition, intermediate between felsic and mafic rocks (for example, andesite)." },
            new Geomaterial { Name="Mafic-composition pyroclastic flows", Description="Hot ash, pumice, and rock fragments erupted from volcano. Material moves downslope commonly in chaotic flows; once deposited, hot fragments may compact under their own weight and weld together. Because of their low silica content and resulting low viscosity, parental magmas tend to erupt gently as lava flows rather than more forcefully as pyroclastic flows. Includes basalt; rocks are com-monly dark-colored." },
            new Geomaterial { Name="Air-fall tephra", Description="Fragments of volcanic rock and lava, of various sizes, carried into air by explosions and by hot gases in eruption columns or lava fountains; known as tephra. As tephra falls to ground, with increasing distance from volcano, average size of individual rock particles and thickness of resulting deposit decrease. Fine tephra deposited at some distance from volcano is known as volcanic ash." },
            new Geomaterial { Name="Felsic-composition air-fall tephra", Description="Fragments of volcanic rock and lava, of various sizes, carried into air by explosions and by hot gases in eruption columns or lava fountains; known as tephra. As tephra falls to ground, with increasing distance from volcano, average size of individual rock particles and thickness of resulting deposit decrease. Because of their high silica content and resulting high viscosity, felsic-composition magmas tend to erupt explosively, readily forming pumice and volcanic ash. Composed of light-colored rocks (for example, rhyolite, dacite, trachyte, latite)." },
            new Geomaterial { Name="Intermediate-composition air-fall tephra", Description="Fragments of volcanic rock and lava, of various sizes, carried into the air by explosions and by hot gases in eruption columns or lava fountains; known as tephra. As tephra falls to ground, with increasing distance from volcano, average size of individual rock particles and thickness of resulting deposit decrease. Parental magma commonly erupts from stratovolcanoes as thick lava flows but also can generate strong explosive eruptions to form pyroclastic flows. Includes rocks that are, in color and mineral composition, intermediate between felsic and mafic rocks (for example, andesite)." },
            new Geomaterial { Name="Mafic-composition air-fall tephra", Description="Fragments of volcanic rock and lava, of various sizes, carried into the air by explosions and by hot gases in eruption columns or lava fountains; known as tephra. As tephra falls to ground, with increasing distance from volcano, average size of individual rock particles and thickness of resulting deposit decrease. Because of their low silica content and resulting low viscosity, parental magmas tend to erupt gently as lava flows, and so these deposits are uncommon. Includes basalt; rocks are commonly dark-colored." },
            new Geomaterial { Name="Lava flows", Description="Lateral, surficial outpourings of molten lava from vent or fissure; also, solidified bodies of rock that form when they cool. Composed generally of fine-grained, dark-colored rocks (for example, basalt), which tend to form extensive sheets that have generally low relief, except in vent areas where cinder cones or shield volcanoes may form. Includes basaltic shield volcanoes, which may become very large (for example, Hawaii)." },
            new Geomaterial { Name="Felsic-composition lava flows", Description="Lateral, surficial outpourings of molten lava from vent or fissure; also, solidi-fied bodies of rock that form when they cool. Because of their high silica content and resulting high viscosity, parental magmas tend to erupt explosively, and so these deposits are uncommon. Includes fine-grained, light-colored rock with rhyolitic, dacitic, trachytic, and latitic composition." },
            new Geomaterial { Name="Intermediate-composition lava flows", Description="Lateral, surficial outpourings of molten lava from vent or fissure; also, solidified bodies of rock that form when they cool. Parental magma commonly erupts from stratovolcanoes as thick lava flows. Includes rocks that are, in color and in mineral composition, intermediate between felsic and mafic rocks (for example, andesite)." },
            new Geomaterial { Name="Mafic-composition lava flows", Description="Lateral, surficial outpourings of molten lava from vent or fissure; also, solidi-fied bodies of rock that form when they cool. Low-silica parental magmas have low viscosity and tend to form extensive sheets that have generally low relief. Includes basaltic shield volcanoes, which may become very large (for example, in Hawaii). Composed of fine-grained, dark rocks, including basaltic." },
            new Geomaterial { Name="Volcanic mass flow", Description="Volcanic deposits formed by mass movement (for example, debris avalanches, debris flows, lahar deposits), in many cases triggered by volcanic eruption. Debris avalanches that occur on volcanoes clearly without eruptive trigger may be classified as sedimentary (for example, as “Debris flows, landslides, and other local-ized mass-movement sediment”)." },
            new Geomaterial { Name="Intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) below Earth’s surface." },
            new Geomaterial { Name="Coarse-grained intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at some depth beneath Earth’s surface, thereby cooling slowly enough for mineral crystals to grow large enough to be visible to naked eye." },
            new Geomaterial { Name="Coarse-grained, felsic-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at some depth beneath Earth’s surface, thereby cooling slowly enough for mineral crystals to grow large enough to be visible to naked eye. Composed mostly of light-colored minerals (for example, feldspar, quartz). Includes granitic, syenitic, and monzonitic rock." },
            new Geomaterial { Name="Coarse-grained, intermediate-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at some depth beneath Earth’s surface, thereby cooling slowly enough for min-eral crystals to grow large enough to be visible to naked eye. Intermediate in color and in mineral composition (between felsic and mafic igneous rock). Includes dioritic rock." },
            new Geomaterial { Name="Coarse-grained, mafic-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at some depth beneath Earth’s surface, thereby cooling slowly enough for mineral crystals to grow large enough to be visible to naked eye. Composed mostly of feldspar and dark-colored minerals. Includes gabbroic rock." },
            new Geomaterial { Name="Ultramafic intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at some depth beneath Earth’s surface, thereby cooling slowly enough for mineral crystals to grow large enough to be visible to naked eye. Composed almost entirely of mafic minerals (for example, hypersthene, augite, olivine)." },
            new Geomaterial { Name="Fine-grained intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at shallow depth beneath Earth’s surface, thereby cooling quickly. Generally fine grained but may contain large min-eral crystals (phenocrysts). Mostly found as tabular dikes or sills." },
            new Geomaterial { Name="Fine-grained, felsic-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at shallow depth beneath Earth’s surface, thereby cooling quickly. Generally fine grained but may contain large mineral crystals (phenocrysts). Mostly found as tabular dikes or sills. Composed mostly of light-colored minerals. Includes rhyolitic, dacitic, trachytic, and latitic rock." },
            new Geomaterial { Name="Fine-grained, intermediate-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at shallow depth beneath Earth’s surface, thereby cooling quickly. Generally fine grained but may contain large mineral crystals (phenocrysts). Mostly found as tabular dikes or sills. Intermediate in color and in mineral composition (between felsic and mafic igneous rock). Includes andesitic rock." },
            new Geomaterial { Name="Fine-grained, mafic-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) at shallow depth beneath Earth’s surface, thereby cooling quickly. Generally fine grained but may contain large mineral crystals (phenocrysts). Mostly found as tabular dikes or sills. Composed mostly of dark-colored minerals. Includes basaltic rock." },
            new Geomaterial { Name="Exotic-composition intrusive igneous rock", Description="Rock that solidified from molten or partly molten material (magma) below Earth’s surface that has exotic mineralogical, textural, or field setting characteristics. Typically dark colored with abundant phenocrysts. Includes kimberlite, lamprophyre, lamproite, and foiditic rocks." },
            new Geomaterial { Name="Igneous and metamorphic rock", Description="Consists of coarse-grained intrusive igneous rock and generally medium- to high-grade metamorphic rock." },
            new Geomaterial { Name="Metamorphic rock", Description="Rock derived from preexisting rocks and altered by essentially solid-state mineralogical, chemi-cal, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical envi-ronment, generally at depth in Earth’s crust." },
            new Geomaterial { Name="Regional metamorphic rock, of unspecified origin", Description="Rock derived from preexisting rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to marked regional changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Origin of preexisting rock is mixed (for example, igneous and sedimentary) or is not known." },
            new Geomaterial { Name="Lower-grade metamorphic rock, of unspecified origin", Description="Rock derived from preexisting rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to relatively mild regional changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Origin of preexisting rock is mixed (for example, igneous and sedimentary) or is not known. Includes slate and phyllite." },
            new Geomaterial { Name="Medium and high-grade regional metamorphic rock, of unspecified origin", Description="Rock derived from preexisting rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to relatively intense regional changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Origin of preexisting rock is mixed (for example, igneous and sedimentary) or is not known. Includes amphibolite, granulite, schist, and gneiss." },
            new Geomaterial { Name="Contact-metamorphic rock", Description="Altered rock that originated by local processes of thermal metamorphism, genetically related to intrusion and extrusion of magmas and taking place at or near contact with body of igneous rock. Metamorphic changes are affected by heat and fluids emanating from magma and by some deformation because of emplacement of igneous mass." },
            new Geomaterial { Name="Deformation-related metamorphic rock", Description="Rock derived from preexisting rocks by essentially solid-state mineralogi-cal, chemical, or structural changes in response to strong deformation, commonly in association with marked changes in temperature, pressure, and (or) chemical environment. Generally forms in narrow, planar zones of local deformation (for example, along faults); characterized by foliation or alignment of mineral grains. Includes mylonite and cataclasite." },
            new Geomaterial { Name="Metasedimentary rock", Description="Rock derived from preexisting sedimentary rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust." },
            new Geomaterial { Name="Slate and phyllite, of sedimentary-rock origin", Description="Fine-grained rock derived from preexisting sedimentary rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Includes phyllite and slate (compact, fine-grained rock that possesses strong cleavage and, hence, can be split into slabs and thin plates). Mostly formed from fine-grained material such as mudstone." },
            new Geomaterial { Name="Schist and gneiss, of sedimentary-rock origin", Description="Foliated rock derived from preexisting sedimentary rocks by essen-tially solid-state mineralogical, chemical, or structural changes in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Includes schist (characterized by such strong foliation or alignment of minerals that it readily splits into flakes or slabs) and gneiss (characterized by alternat-ing, irregular bands of different mineral composition). Mostly formed from fine-grained material such as mudstone." },
            new Geomaterial { Name="Meta-carbonate rock", Description="Rock derived from preexisting carbonate sedimentary rocks and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Characterized by recrystallization of carbonate minerals in source rock. Includes marble (for which preexisting rock was dominantly limestone or other rock composed of calcite), dolomitic marble, meta-dolostone, and meta-dolomite (for which preexisting rock contained appreciable amount of magnesium)." },
            new Geomaterial { Name="Quartzite", Description="Rock derived from preexisting quartz-rich sedimentary rocks (commonly sandstone) and altered by essentially solid-state mineralogical, chemical, or structural changes, in response to marked changes in temperature, pressure, shear stress, and (or) chemical environment, generally at depth in Earth’s crust." },
            new Geomaterial { Name="Metaigneous rock", Description="Rock derived from preexisting igneous rocks and altered by essentially solid-state, mineralogical, chemical, or structural changes, in response to marked changes in temperature, pressure, shear stress, and (or) chemical environment, generally at depth in Earth’s crust." },
            new Geomaterial { Name="Meta-ultramafic rock", Description="Rock derived from preexisting ultramafic rocks by essentially solid-state, mineralogi-cal, chemical, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Composed mostly of magnesium-bearing minerals (for example, serpentine, talc, magnesite)." },
            new Geomaterial { Name="Meta-mafic rock", Description="Rock derived from preexisting mafic rocks by essentially solid-state, mineralogical, chemi-cal, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Composed mostly of iron- and magnesium-bearing, dark-colored and (or) green minerals. Includes greenstone, amphibolite, and metagabbro." },
            new Geomaterial { Name="Meta-felsic and intermediate rock", Description="Rock derived from preexisting felsic and intermediate-composition rocks by essentially solid-state, mineralogical, chemical, or structural changes, in response to marked changes in tempera-ture, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Composed mostly of light-colored minerals; relatively enriched in silica. Includes metagranite, metadiorite, and meta-andesite." },
            new Geomaterial { Name="Meta-volcaniclastic rock", Description="Rock derived from preexisting volcaniclastic rocks by essentially solid-state, miner-alogical, chemical, or structural changes, in response to marked changes in temperature, pressure, deformation, and (or) chemical environment, generally at depth in Earth’s crust. Composed of deformed but recognizable particles or clasts of volcanic explosive material." },
            new Geomaterial { Name="Other materials:" },
            new Geomaterial { Name="Rock and sediment", Description="Various rocks and sediment, not differentiated." },
            new Geomaterial { Name="Rock", Description="Various rock types, not differentiated." },
            new Geomaterial { Name="“Made” or human-engineered land", Description="Modern, unconsolidated material known to have human-related origin." },
            new Geomaterial { Name="Water or ice" },
            new Geomaterial { Name="Unmapped area" },
        };
    }
}
